import { defineStore } from 'pinia'

function mockItems() {
  const groups = ['组1', '组2', '组3']
  return Array.from({ length: 24 }).map((_, i) => ({
    id: i + 1,
    name: `示例商品 ${i + 1}`,
    price: (Math.random() * 200 + 10).toFixed(2),
    cover: `https://picsum.photos/seed/i${i}/300/200`,
    group: groups[i % groups.length],
    desc: '这是一段商品简介，占位文字用于展示效果。'
  }))
}

export const useItemStore = defineStore('item', {
  state: () => ({
    loading: false,
    items: [],
    keyword: '',
    groupSelected: '全部'
  }),
  getters: {
    filteredItems(state) {
      return state.items.filter(it => {
        const kwOk = !state.keyword || it.name.toLowerCase().includes(state.keyword.toLowerCase())
        const grpOk = state.groupSelected === '全部' || it.group === state.groupSelected
        return kwOk && grpOk
      })
    },
    groups() { return ['全部', ...new Set(this.items.map(it => it.group))] }
  },
  actions: {
    async fetchItems() {
      this.loading = true
      await new Promise(r => setTimeout(r, 400))
      this.items = mockItems()
      this.loading = false
    },
    setKeyword(v) { this.keyword = v },
    setGroup(v) { this.groupSelected = v }
  }
})

import { defineStore } from 'pinia'

function genMockOrders() {
  const status = ['已支付', '待支付', '已发货', '已完成']
  return Array.from({ length: 12 }).map((_, i) => ({
    id: (i + 1).toString(),
    number: `LB${String(20251100 + i)}`,
    status: status[i % status.length],
    amount: Number((Math.random() * 500 + 20).toFixed(2)),
    user: {
      name: `用户${i + 1}`,
      email: `user${i + 1}@mail.com`,
      phone: `1380000${String(100 + i)}`,
    },
    createdAt: new Date(Date.now() - i * 86400000).toISOString(),
    items: Array.from({ length: (i % 3) + 1 }).map((__, j) => ({
      sku: `SKU-${i + 1}-${j + 1}`,
      name: `订单商品 ${i + 1}-${j + 1}`,
      price: Number((Math.random() * 200 + 10).toFixed(2)),
      qty: (j % 2) + 1,
      cover: `https://picsum.photos/seed/o${i}${j}/120/80`,
    }))
  }))
}

export const useOrderStore = defineStore('order', {
  state: () => ({ orders: genMockOrders(), keyword: '' }),
  getters: {
    filtered(state) {
      if (!state.keyword) return state.orders
      const kw = state.keyword.toLowerCase()
      return state.orders.filter(o =>
        o.number.toLowerCase().includes(kw) ||
        o.user.email.toLowerCase().includes(kw) ||
        o.user.phone.toLowerCase().includes(kw)
      )
    },
    byId: (state) => (id) => state.orders.find(o => o.id === id),
  },
  actions: {
    setKeyword(v) { this.keyword = v }
  }
})

