import { defineStore } from 'pinia'

export const useHomeStore = defineStore('home', {
  state: () => ({
    pageData: {
      announcement: 'aaa',
      header: {
        title: '发现好物，享受生活',
        subtitle: '简洁优雅的小店页面示例，支持商家自定义分组浏览。'
      },
      searchForm: {
        keyword: '',
        groupName: '全部'
      },
      groups: [],
      items: []
    },
    loading: false
  }),
  getters: {
    groups(state) {
      const list = state.pageData.groups || []
      return list.length ? ['全部', ...list.map(g => g.name)] : ['全部']
    }
  },
  actions: {
    async fetchHomeData() {
      this.loading = true
      try {
        // TODO: 换成真实接口，例如：
        // const { data } = await api.getHomeData(this.pageData.searchForm)
        // this.pageData = data.pageData

        const allGroups = [
          {
            name: '组1',
            items: [
              { id: 1, name: '示例商品 1', price: 19.9, cover: 'https://picsum.photos/seed/i1/300/200', desc: '示例描述1' },
              { id: 2, name: '示例商品 2', price: 29.9, cover: 'https://picsum.photos/seed/i2/300/200', desc: '示例描述2' }
            ]
          },
          {
            name: '组2',
            items: [
              { id: 3, name: '示例商品 3', price: 39.9, cover: 'https://picsum.photos/seed/i3/300/200', desc: '示例描述3' }
            ]
          }
        ]

        const { keyword, groupName } = this.pageData.searchForm
        const kw = keyword.trim().toLowerCase()

        const items = allGroups
          .filter(g => groupName === '全部' || g.name === groupName)
          .flatMap(g =>
            (g.items || [])
              .filter(it => !kw || it.name.toLowerCase().includes(kw))
              .map(it => ({ ...it, groupName: g.name }))
          )

        this.pageData.groups = allGroups
        this.pageData.items = items
      } finally {
        this.loading = false
      }
    },
    setKeyword(keyword) {
      this.pageData.searchForm.keyword = keyword
    },
    async setGroup(groupName) {
      this.pageData.searchForm.groupName = groupName
      await this.fetchHomeData()
    }
  }
})

import { defineStore } from 'pinia'

export const useHomeStore = defineStore('home', {
  state: () => ({
    pageData: {
      announcement: 'aaa',
      header: {
        title: '发现好物，享受生活',
        subtitle: '简洁优雅的小店页面示例，支持商家自定义分组浏览。'
      },
      groups: []
    },
    // 列表相关数据（含搜索表单 + 搜索结果）
    itemData: {
      searchForm: {
        keyword: '',
        groupName: '全部',
        pageNum: 1,
        pageSize: 20
      },
      items: []
    },
    loading: false
  }),
  getters: {
    groups(state) {
      const list = state.pageData.groups || []
      return list.length ? ['全部', ...list.map(g => g.name)] : ['全部']
    }
  },
  actions: {
    async fetchHomeData() {
      this.loading = true
      try {
        // TODO: 换成真实接口，例如：
        // const { data } = await api.getHomeData(this.itemData.searchForm)
        // this.pageData = data.pageData
        // this.itemData = data.itemData

        const allGroups = [
          {
            name: '组1',
            items: [
              { id: 1, name: '示例商品 1', price: 19.9, cover: 'https://picsum.photos/seed/i1/300/200', desc: '示例描述1' },
              { id: 2, name: '示例商品 2', price: 29.9, cover: 'https://picsum.photos/seed/i2/300/200', desc: '示例描述2' }
            ]
          },
          {
            name: '组2',
            items: [
              { id: 3, name: '示例商品 3', price: 39.9, cover: 'https://picsum.photos/seed/i3/300/200', desc: '示例描述3' }
            ]
          }
        ]

        const { keyword, groupName } = this.itemData.searchForm
        const kw = keyword.trim().toLowerCase()

        const items = allGroups
          .filter(g => groupName === '全部' || g.name === groupName)
          .flatMap(g =>
            (g.items || [])
              .filter(it => !kw || it.name.toLowerCase().includes(kw))
              .map(it => ({ ...it, groupName: g.name }))
          )

        this.pageData.groups = allGroups
        this.itemData.items = items
      } finally {
        this.loading = false
      }
    },
    setKeyword(keyword) {
      this.itemData.searchForm.keyword = keyword
      // 重置到第一页
      this.itemData.searchForm.pageNum = 1
    },
    async setGroup(groupName) {
      this.itemData.searchForm.groupName = groupName
      // 切换分组时也重置到第一页
      this.itemData.searchForm.pageNum = 1
      await this.fetchHomeData()
    }
  }
})

import { defineStore } from 'pinia'

function mockItems() {
  const groups = ['组1', '组2', '组3']
  return Array.from({ length: 24 }).map((_, i) => ({
    id: i + 1,
    name: `示例商品 ${i + 1}`,
    price: (Math.random() * 200 + 10).toFixed(2),
    cover: `https://picsum.photos/seed/i${i}/300/200`,
    group: groups[i % groups.length],
    desc: '这是一段商品简介，占位文字用于展示效果。'
  }))
}

export const useItemStore = defineStore('item', {
  state: () => ({
    loading: false,
    items: [],
    keyword: '',
    groupSelected: '全部'
  }),
  getters: {
    filteredItems(state) {
      return state.items.filter(it => {
        const kwOk = !state.keyword || it.name.toLowerCase().includes(state.keyword.toLowerCase())
        const grpOk = state.groupSelected === '全部' || it.group === state.groupSelected
        return kwOk && grpOk
      })
    },
    groups() { return ['全部', ...new Set(this.items.map(it => it.group))] }
  },
  actions: {
    async fetchItems() {
      this.loading = true
      await new Promise(r => setTimeout(r, 400))
      this.items = mockItems()
      this.loading = false
    },
    setKeyword(v) { this.keyword = v },
    setGroup(v) { this.groupSelected = v }
  }
})

